import React from 'react'
import './card.css'
import {Card,Button,Row, Container} from 'react-bootstrap'
const foods=[
    {
        id:1,
        name:'Idli',
        price:35,
        desc:'idly is good healthy breakfast.',
        img:'data:image/jpeg;base64,'
    },
    {
        id:2,
        name:'Dosa',
        price:45,
        desc:'Dosa is very delicious breakfast.',
        img:'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTV__-3abmbiyCo3JjXlzLESUd_jmvbgQ62Rw&usqp=CAU'
    },
    {
        id:3,
        name:'briyani',
        price:300,
        desc:'indias favorite food is briyani',
        img:'data:image/jpeg;base64,'
    },
    {
        id:4,
        name:'maggi',
        price:25,
        desc:'maggi is a noodle type instant food.',
        img:'data:image/jpeg;base64,'
    },
 
]
function Cards() {
    const [counter,setCounter]=React.useState(0)
    const [color,setColor]=React.useState('')
    const handleIncrement=()=>{
        setCounter(counter+1)
    }
    const handleDecrement=()=>{
        setCounter(counter-1)
    }
    return (
        <>
        <h1>Combos</h1>
        <Container className='container'>
            <Row style={{gap:'80px'}}>
                {foods.map(food=>(
                    <>
                    <Card className='card' style={{width:'16rem'}}>
                        <Card.Img variant="top" className='image' src={food.img} />
                        <Card.Body>
                            <Card.Title>{food.name}</Card.Title>
                            <Card.Text>
                                {food.desc}
                            </Card.Text>
                            <Card.Text>
                                Price: {food.price}
                            </Card.Text>
                           
                        </Card.Body>
                    </Card>
                     </>
                ))}
                    

            </Row>
        </Container>
        <div style={{padding:'20px'}}>
        <button onClick={handleIncrement}>+</button>
        <button>Add to cart:{counter}</button>
        <button onClick={handleDecrement}>-</button>
        <h2>@ the price of  ₹150 only /-</h2>

        <p>Change color <p style={{color}}>{color}</p></p>
        
        <Button className='btn' onClick={()=>setColor('red')}>Red</Button>
        <Button className='btn' onClick={()=>setColor('blue')}>Blue</Button>
        <Button className='btn' onClick={()=>setColor('green')}>Green</Button>
        <Button className='btn' onClick={()=>setColor('orange')}>Orange</Button>
        </div>
        </>
    )



}


     
        
 
export default Cards
